using Microsoft.EntityFrameworkCore.Metadata.Builders;
using Prime.Models;

namespace Prime.Configuration.Database
{
    public class EmailTemplateConfiguration : SeededTable<EmailTemplate>
    {
        public static readonly DateTime EnrolleeRenewalRequiredUpdateAug17th2022 = new(2022, 8, 17, 8, 0, 0, DateTimeKind.Utc);
        public static readonly DateTime MultipleEmailContentUpdateNov30th2022 = new(2022, 11, 30, 8, 0, 0, DateTimeKind.Utc);
        public static readonly DateTime EmailContentUpdateOct27th2023 = new(2023, 10, 27, 8, 0, 0, DateTimeKind.Utc);
        public static readonly DateTime EmailContentUpdateMar5th2024 = new(2024, 3, 5, 8, 0, 0, DateTimeKind.Utc);

        public override IEnumerable<EmailTemplate> SeedData
        {
            get
            {
                return new[] {
                    new EmailTemplate { Id = 1, EmailType = EmailTemplateType.EnrolleeStatusChange, TemplateName = "Enrollee Status Change", Subject = "PRIME Requires your Attention", Template = "Hello, <p><p> Your PRIME application status has changed since you last viewed it. Please click <a href=\"@Model.Url\">here</a> to log into PRIME and view your status. </p><p> You may need to share your approval email to get your PharmaNet access is set up. Please connect by phone or email if you have any questions. </p><p> Thank you, </p><p> PRIME Support team<br/>1-844-397-7463 <br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when the adjudicator approves the enrolment, or changes the status to editable.", Recipient = "To: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 2, EmailType = EmailTemplateType.CommunityPracticeNotification, TemplateName = "Community Practice Notification", Subject = "New Access Request", Template = "<style> .underline { text-decoration: underline; } .list-item-mb { margin-bottom: 0.75rem; } </style>Hello:<br/><br/>@Model.EnrolleeFullName has been approved for private community health practice access to PharmaNet.<br/><br/><strong> To set up their access, forward this notification and the information below to <span class=\"underline\">whoever sets up PharmaNet user accounts. This is usually your PharmaNet software vendor but can also be someone like an IT department or authorized individual</span>. </strong><br/><br/><ol><li class=\"list-item-mb\"> Name of the community health practice ___________________ </li><li class=\"list-item-mb\"> Practice address ___________________ </li><li class=\"list-item-mb\"> PharmaNet site ID, if you have it ___________________</li></ol> If @Model.EnrolleeFullName accesses PharmaNet on behalf of another user, the PharmaNet software vendor should know who they are, and they should be enrolled in PRIME.<br/><br/> Your software vendor can find @Model.EnrolleeFullName's details by following this link: <a href=\"@Model.TokenUrl\">@Model.TokenUrl</a> <strong>This link will expire in 10 days.</strong><br/><br/>Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "Enrollee send out the Provisioner Link Email and access instructions.", Recipient = "To: Provisioner, CC: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 3, EmailType = EmailTemplateType.CommunityPharmacyNotification, TemplateName = "Community Pharmacy Notification", Subject = "New Access Request", Template = "Hello, <br/> <br/> @Model.EnrolleeFullName has been approved for <strong>community pharmacy access to PharmaNet.</strong> Their PharmaNet access account can now be set up in your local software. You must include their <strong>global PharmaNet ID (GPID)</strong> on their account profile. You can access their GPID at the link below. <br/><br/> If @Model.EnrolleeFullName accesses PharmaNet on behalf of another user, the PharmaNet software vendor should know who they are, and they should be enrolled in PRIME.<br/> <br/> <a href=\"@Model.TokenUrl\">@Model.TokenUrl</a> <br/> <strong>This link will expire after @Model.ExpiresInDays days</strong>. <br/> <br/>Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "Triggered by the enrollee; the Provisioner Link Email for PharmaNet access at a community pharmacy.", Recipient = "To: Provisioner, CC: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 4, EmailType = EmailTemplateType.HealthAuthorityNotification, TemplateName = "Health Authority Notification", Subject = "New Access Request", Template = "To: PharmNet administrator <br/> <br/> @Model.EnrolleeFullName has been approved for <strong>health authority access to PharmaNet.</strong> <br/> They can now be set up with their PharmaNet access account in your local software. Their <strong>Global PharmaNet ID (GPID)</strong> must be on their account profile. <br/><br/> You can access their GPID here: <a href=\"@Model.TokenUrl\">@Model.TokenUrl</a> <br/> <strong>This link will expire after @Model.ExpiresInDays days</strong>. <br/><br/>Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "Enrollee triggered Provisioner Link Email for health authority access.", Recipient = "To: Provisioner, CC: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 5, EmailType = EmailTemplateType.RemoteUserNotification, TemplateName = "Remote User Notification", Subject = "Remote Practitioner Notification", Template = "Hello,<p><p>The Ministry of Health has been notified that you require remote access to PharmaNet at:<br/> <br/> Organization name: @Model.OrganizationName <br/> Site address: @Model.SiteStreetAddress, @Model.SiteCity <br/> <br/>To access PharmaNet remotely, you must enrol in PRIME and indicate that you require remote access. If you have already enrolled in PRIME, you must log into PRIME and add remote access to your profile. <p><p>**Remote access is only available for practitioners working in a private community health practice that has included the practitioner as a remote user in the PRIME site registration for that practice. Remote access means you are physically located outside the premises of an approved PharmaNet site. <span class=\"text-danger\">You must always be physically located in BC when using PharmaNet, even if approved for remote access.</span> <p>If you do not require remote access to the site identified above, please advise the site so they can remove you from their remote user list in PRIME.</p><p><p>Once you have logged into to PRIME: <p><p> - Click the toggle to request remote access if you require it. <p><p> - Click Save and Continue. Refer to <a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/pharmacare/pharmanet-bc-s-drug-information-network/prime/prime-user-guides#update\">update information or renew enrolment</a> for instructions. </p><p>You can enrol or update your profile at <a href=\"@Model.PrimeUrl\">@Model.PrimeUrl</a>.</p> Please connect by email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/> PRIMESupport@gov.bc.ca</br> <a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/pharmacare/pharmanet-bc-s-drug-information-network/prime/prime-user-guides\">PRIME user guides</a>", Description = "The email will be triggered when an adjudicator approves a community site with remote user.", Recipient = "To: Remote User", ModifiedDate = EmailContentUpdateMar5th2024, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateMar5th2024 },
                    new EmailTemplate { Id = 6, EmailType = EmailTemplateType.RemoteUserUpdatedNotification, TemplateName = "Remote User Updated Notification", Subject = "Remote Practitioners Changed", Template = "Hello, <p><p>@{ var pecText = string.IsNullOrWhiteSpace(Model.SitePec) ? \"Not Assigned\" : Model.SitePec; } <p> Notification: The list of Remote Practitioners at @Model.SiteStreetAddress of @Model.OrganizationName (PEC: @pecText) has been updated. <br/><br/>The remote practitioners at this site are: </p> <h2 class=\"mb-2\">Remote Users</h2> @foreach (var name in Model.RemoteUserNames) { <div class=\"ml-2 mb-2\"> <h5>Name</h5> <span class=\"ml-2\">@name</span> </div> } <h2 class=\"mb-2\">Site Information</h2> <p> See the attached registration and organization agreement for more information. @if (!string.IsNullOrWhiteSpace(Model.DocumentUrl)) { @(\"To access the Business Licence, click this\") <a href=\"@Model.DocumentUrl\" target=\"_blank\">link</a>@(\".\") } </p> <br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when the remote user has been updated and the site is completed.", Recipient = "To: HLTH.HnetConnection@gov.bc.ca & primesupport@gov.bc.ca", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 7, EmailType = EmailTemplateType.HIBCSiteSubmission, TemplateName = "HIBC Site Submission", Subject = "[{siteId}] Site Registration Approved", Template = "Hello, <p><p>@Model.DoingBusinessAs with SiteID @Model.Pec has been approved by the Ministry of Health for PharmaNet access. Please notify the PharmaNet software vendor for this site and complete any remaining tasks to activate the site.</p><p>Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when a site is approved.", Recipient = "To: HLTH.HnetConnection@gov.bc.ca", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 8, EmailType = EmailTemplateType.EnrolleeRenewalRequired, TemplateName = "Enrollee Renewal Required", Subject = "Renew Your PRIME Enrolment", Template = "@{ var renewalDate = Model.RenewalDate.Date.ToShortDateString(); } <p>Hello  @Model.EnrolleeName,</p><p>  If you still need PharmaNet access you must renew your PRIME enrolment annually. </p><p>Please renew by @renewalDate.</p><ol><li>Log in to PRIME <a href=\"@Model.PrimeUrl\">@Model.PrimeUrl</a>.</li><li>Click <b>Access Individual Enrolment</b>.</li><li>On the PRIME Profile screen, review your information.<ol style=\"list-style-type:lower-alpha;\"><li>Select the edit button to revise sections that are out of date. Click <strong>Continue</strong> at the bottom of updated screens to save changes.</li><li>Once changes are saved, check the <strong>\"I certify\"</strong> box and click the <strong>Submit Enrolment</strong> button.</li></ol></li><li>If instructed to go on, click <b>Continue.</b></li><li>If you are notified that your renewal is approved, go to the next screen to review and accept the PharmaNet user terms of access. This step completes your renewal. The terms of access may have changed, so please read carefully.</li> If your renewal is sent for review, you will either be contacted by the PRIME Support team or notified to log in to PRIME to complete the remaining steps.<li> You will be presented with and must accept the user terms of access. Please note these may have changed since you last accepted them; review them carefully. <li>You may need to share your PRIME approval email with the person or team in your workplace who sets up PharmaNet accounts if something has changed since you last updated your account, for example: name change, access type, care setting, college license information. You do this by entering their email address(es) on the Share Approval Email screen and clicking the send button. <ol style=\"list-style-type:lower-alpha;\"> </li></ol></li></ol><p>Please connect by phone or email if you have any questions. <br/>Thank you for renewing your PRIME enrolment.</p> <br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca <p><a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/pharmacare/pharmanet-bc-s-drug-information-network/prime/prime-user-guides\">PRIME user guides</a>", Description = "The email will be triggered daily at 3pm in a batch process. The email will be sent to the enrollee, whose ToA expiry date is coming in 14,  7, 3, 2, 1, 0 days.", Recipient = "To: Enrollee", ModifiedDate = EmailContentUpdateMar5th2024, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateMar5th2024 },
                    new EmailTemplate { Id = 9, EmailType = EmailTemplateType.BusinessLicenceUpload, TemplateName = "Site Business Licence Upload", Subject = "Site Business Licence Uploaded", Template = "Hello,<p><p> A user has uploaded a business licence to their PharmaNet site registration. @if (!string.IsNullOrWhiteSpace(Model.Url)) { @(\"To access the Business Licence, click this\") <a href=\"@Model.Url\" target=\"_blank\">link</a>@(\".\") } </p> Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when SA uploads a business license and clicks 'Save and Continue'.", Recipient = "To: Adjudicator", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 10, EmailType = EmailTemplateType.EnrolleeRenewalPassed, TemplateName = "Forced Renewal Passed Notification", Subject = "Your PRIME Renewal Date Has Passed", Template = "Hello @Model.EnrolleeName,<br/><br/><p>Your PRIME renewal is overdue.</p><p>  If you still need access to PharmaNet to care for patients, renew your PRIME enrolment now by logging in to PRIME at <a href='@Model.PrimeUrl'>@Model.PrimeUrl</a>. Update your information as needed, submit, and then read and sign the terms of access. PRIME profiles must be renewed annually.</p><p> Add us to your email contacts to make sure our emails don't land in your spam folder.<br/>Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered daily at 3pm in a batch process. The email will be sent to the enrollee, whose ToA has expired.", Recipient = "To: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 11, EmailType = EmailTemplateType.SiteApprovedPharmaNetAdministrator, TemplateName = "Site Approved Pharma Net Administrator", Subject = "Site Registration Approved", Template = "<p> Your site registration has been approved. The site will now be set up and activated in PharmaNet. Your PharmaNet software vendor will be notified when the site has been activated, and you will hear from them when you access PharmaNet. </p> <p> Individuals who will be accessing PharmaNet at your site should enrol in PRIME now if they have not already done so. For more information, please visit <a href=\"https://www.gov.bc.ca/pharmanet/PRIME\" target=\"_blank\">https://www.gov.bc.ca/pharmanet/PRIME</a>. </p> Private community practice only: Physicians or nurse practitioners must indicate in their PRIME profile that they require remote access if needed. They can do this here: <a href=\"https://pharmanetenrolment.gov.bc.ca\" target=\"_blank\">https://pharmanetenrolment.gov.bc.ca</a>. </p> <p> Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "(To be Deleted - Disabled) When Admin/Ajudicator approves a site.", Recipient = "To: Site PharmaNet Admin", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 12, EmailType = EmailTemplateType.SiteApprovedSigningAuthority, TemplateName = "Site Approved Signing Authority", Subject = "Site Registration Approved", Template = "<p> The site you registered in PRIME, @Model.DoingBusinessAs, has been approved by the Ministry of Health. Your SiteID is @Model.Pec. </p> <p> Health Insurance BC has been notified of the site’s approval and will contact your software vendor. Your vendor will be notified of your site activation. As they complete any remaining setup for your site, they may reach out for additional information. </p> <p> If you need to update any information in PRIME regarding your site, you may log in at any time using your BC Services Card App. If you have any questions or concerns, please phone 1-844-397-7463 or email PRIMESupport@gov.bc.ca. </p> Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "(To be Deleted - Disabled) When Admin/Ajudicator approves a site.", Recipient = "To: Signing Authority", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 13, EmailType = EmailTemplateType.SiteRegistrationSubmission, TemplateName = "Site Registration Submission", Subject = "[{siteId}] {isNewPrefix} PRIME Site Registration Submission - {careSetting}", Template = "Hello,<p> A new PharmaNet site registration has been received. See the attached registration and organization agreement for more information. @if (!string.IsNullOrWhiteSpace(Model.Url)) { @(\"To access the Business Licence, click this\") <a href=\"@Model.Url\" target=\"_blank\">link</a>@(\".\") } <br/><br/> Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when a SA or AU submits a site. In the subject line, “{isNewPrefix}” will be replaced with “Priority! New Pharmacy” if the site is marked as a new pharmacy. Also, “{careSetting}” will be replaced with  the site care setting, and “{siteId}” will be replaced with the internal site ID", Recipient = "To: HLTH.HnetConnection@gov.bc.ca, cc: primesupport@gov.bc.ca", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 14, EmailType = EmailTemplateType.OrganizationClaimApprovalNotification, TemplateName = "Organization Claim Approval Notification", Subject = "Organization Claim was Approved", Template = "Hello, <p><p>Your claim of the organization @Model.OrganizationName, which the site with SiteID/PEC @Model.ProvidedSiteId is part of, has been approved.  You may now access to site registration for this organization. <br/><br/>You must sign the Organization Agreement before you will be able to update or add any sites. This is a click-to-accept online signature that is linked to your BC Services Card identity. If you are not qualified to accept legal agreements on behalf of your organization, or if your organization does not allow you to sign online agreements, please contact PRIMESupport@gov.bc.ca for further guidance before you proceed further. <p><p> Thank you, <p><p> PRIME Support Team<br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when PRIME admin approves a organization claim.", Recipient = "To: New SA", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 15, EmailType = EmailTemplateType.SiteReviewedNotification, TemplateName = "Site Reviewed Notification", Subject = "PRIME Site Registration review complete", Template = "Hello, <p><p>A PRIME Admin has reviewed the site registration for PEC/SiteID# @Model.Pec. @(!string.IsNullOrWhiteSpace(Model.Note) ? $\"The following notes were added: {Model.Note}\" : \"\") <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered when an adjudicator sends a notification from the site overview page for Community Pharmacy or Device Provider site.", Recipient = "To: Lori.Haggstrom@gov.bc.ca", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 16, EmailType = EmailTemplateType.SiteActiveBeforeRegistrationSubmission, TemplateName = "Site Active Before Registration Submission", Subject = "PRIME Site Registration Submission", Template = "Thank you for registering your site (SiteID: @Model.Pec) in PRIME. If you need to update any site information in PRIME, you may log in at any time using your BC Services Card app. Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "(To be Deleted) The email will be triggered when a community site is approved and it is marked as 'Active Before Registration'.", Recipient = "To: SA", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 17, EmailType = EmailTemplateType.PaperEnrolleeSubmission, TemplateName = "Paper Enrollee Submission", Subject = "Paper Enrolment Submission", Template = "Your request for PharmaNet access has been approved and recorded in PRIME. When it is possible for you to do so, you must enrol in PRIME using your BC Services Card app. <br/> <br/> <strong> Your temporary GPID is @Model.GPID. </strong> <br/> <br/> The first time you log in to PRIME, you should be asked if you have previously received permission to access PharmaNet via an offline process. If you do not see this prompt, please stop your enrolment and contact <a href=\"mailto:PRIMEsupport@gov.bc.ca\" target=\"_top\">PRIMEsupport@gov.bc.ca</a><br/><br/>Thank you,<br/> <br/>PRIME Support <br/>1-844-397-7463<br/><a href=\"mailto:PRIMEsupport@gov.bc.ca\" target=\"_top\">PRIMEsupport@gov.bc.ca</a>", Description = "(To be Deleted) The email will be triggered when a paper enrolment is submitted.", Recipient = "To: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 18, EmailType = EmailTemplateType.EnrolleeUnsignedToa, TemplateName = "Enrollee Unsigned Toa", Subject = "PharmaNet Terms of Access requires signing", Template = "Dear @Model.EnrolleeName, <br/> <br/><p> Please log in to PRIME and accept your PharmaNet terms of access to complete your enrolment.</p><p>You can access PRIME here <a href='@Model.PrimeUrl'>@Model.PrimeUrl</a>.  If you are not directed to the terms of access, select \"Terms of Access\" from the menu on the lefthand side of the screen.</p>Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "(Disabled) The email will be triggered by a batch process, and the email will send to the enrollee, who has not signed the ToA.", Recipient = "To: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 19, EmailType = EmailTemplateType.EnrolleeAbsenceNotification, TemplateName = "Enrollee Absence Notification", Subject = "PRIME Absence Notification", Template = "Hello:<p><p>This is an automated generated email from PRIME. <br/> <br/> @(Model.FirstName + \" \" +  Model.LastName + \" is going to be absent \") @if (Model.End.HasValue) {@(\"from \" + Model.Start.ToShortDateString() + \" to \" + Model.End.Value.ToShortDateString() + \".  Please consider deactivating the user during this period. Any access during this period by the user will be considered as an unauthorized access.\")} else {@(\"indefinitely, starting \" + Model.Start.ToShortDateString() + \". Please deactivate the user on the start date. Any access during this period by the user will be considered as an unauthorized access.\")} Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "Enrollee send email notification for the absence entry/ period of deactivation.", Recipient = "To: User entered email", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 20, EmailType = EmailTemplateType.ForcedRenewalNotification, TemplateName = "Forced Renewal Notification", Subject = "PRIME Renewal Required", Template = "Hello @Model.EnrolleeName, <br/> <br/> You are receiving this message as your PharmaNet user status has changed from on-behalf-of user to independent user following changes to legislation governing access to PharmaNet. <br/> <br/> Being an independent PharmaNet user means you will now access PharmaNet as yourself instead of on behalf of another practitioner. <br/> <br/> Log back in to PRIME by @Model.RenewalDate.ToString(\"d MMMM yyyy\") to confirm your profile information and review and accept the user terms of access for independent users. These will automatically be presented to you. This is a requirement for you to maintain access to PharmaNet, as the new terms of access are different from those you may have accepted earlier. <br/> <br/> For information about PRIME, visit the <a href=\"@Model.PrimeUrl\">PRIME web page</a>.  If you have questions or difficulties using PRIME, please contact us at the email address below.<br/> <br/> Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered daily at 3pm in a batch process. The email will be sent to the enrollee, whose ToA expiry date is coming in 14,  7, 3, 2, 1, 0 days and it is marked as forced renewal.", Recipient = "To: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 21, EmailType = EmailTemplateType.ForcedRenewalPassedNotification, TemplateName = "Enrollee Renewal Passed", Subject = "Your PRIME Renewal Date Has Passed", Template = "Hello @Model.EnrolleeName, <br/> <br/> <b>This is your last day</b> to log back in to PRIME to confirm your profile information and review and accept the new user terms of access for independent users. These will automatically be presented to you. This is a requirement for you to maintain access to PharmaNet. <br/><br/>You are receiving this message as your PharmaNet user status has changed from on-behalf-of user to independent user following changes to legislation governing access to PharmaNet. <br/> <br/> Being an independent user of PharmaNet means you will now access PharmaNet as yourself instead of on behalf of another practitioner. <br/>  <br/> For information about PRIME, visit the <a href=\"@Model.PrimeUrl\">PRIME web page</a>. Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "The email will be triggered daily at 3pm in a batch process. The email will be sent to the enrollee, whose ToA has expired and it is marked as forced renewal.", Recipient = "To: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },
                    new EmailTemplate { Id = 22, EmailType = EmailTemplateType.DeviceProviderNotification, TemplateName = "Device Provider Notification", Subject = "New Access Request", Template = "Hello: <br/> <br/> @Model.EnrolleeFullName has been approved for <strong>device provider access to PharmaNet.</strong> Their PharmaNet access account can now be set up in your local software. You must include their <strong>Global PharmaNet ID (GPID)</strong> on their account profile. You can access their GPID at the link below. <br/> <br/> <a href=\"@Model.TokenUrl\">@Model.TokenUrl</a> <br/> <strong>This link will expire after @Model.ExpiresInDays days</strong>. <br/> <br/> Please connect by phone or email if you have any questions. <br/><br/>Thank you, <br/><br/>PRIME Support team <br/>1-844-397-7463<br/> PRIMESupport@gov.bc.ca", Description = "Enrollee send out the Provisioner Link Email and access instructions.", Recipient = "To: Provisioner, CC: Enrollee", ModifiedDate = SEEDING_DATE, CreatedTimeStamp = SEEDING_DATE, UpdatedTimeStamp = EmailContentUpdateOct27th2023 },                };
            }
        }
        public override void Configure(EntityTypeBuilder<EmailTemplate> builder)
        {
            builder
                .HasIndex("EmailType")
                .IsUnique();

            builder.HasData(SeedData);
        }
    }
}
