    public partial class LPNTOA : Migration
    {
        protected override void Up(MigrationBuilder migrationBuilder)
        {
            migrationBuilder.InsertData(
                table: "AgreementVersion",
                columns: new[] { "Id", "AgreementType", "CreatedTimeStamp", "CreatedUserId", "EffectiveDate", "Text", "UpdatedTimeStamp", "UpdatedUserId" },
                values: new object[] { 28, 9, new DateTimeOffset(new DateTime(2023, 5, 19, 10, 0, 38, 709, DateTimeKind.Unspecified).AddTicks(9025), new TimeSpan(0, -7, 0, 0, 0)), new Guid("00000000-0000-0000-0000-000000000000"), new DateTimeOffset(new DateTime(2023, 5, 19, 10, 0, 38, 709, DateTimeKind.Unspecified).AddTicks(8921), new TimeSpan(0, -7, 0, 0, 0)), "<h1>\r\n  PharmaNet Licenced Practical Nurse Registrant Terms of Access\r\n</h1>\r\n\r\n<p class=\"bold\">\r\n  By enrolling for PharmaNet access, you agree to the following terms (the &quot;Agreement&quot;). Please read them\r\n  carefully.\r\n</p>\r\n\r\n<p class=\"bold underline\">\r\n  Licenced Practical Nurse Registrant Access\r\n</p>\r\n\r\n<ol>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      BACKGROUND\r\n    </p>\r\n    <p>\r\n      The Province owns and is responsible for the operation of PharmaNet, the province- wide network that links all\r\n      health\r\n      care providers in British Columbia to a central data system. Every prescription dispensed in community pharmacies\r\n      is\r\n      entered into PharmaNet.\r\n    </p>\r\n    <p>\r\n      The purpose of providing you with access to PharmaNet is to enhance patient care by providing timely and relevant\r\n      information to persons involved in the provision of direct patient care.\r\n    </p>\r\n    <p>\r\n      PharmaNet contains highly sensitive confidential information, including Personal Information and the proprietary\r\n      and\r\n      confidential information of third- party licensors to the Province, and it is in the public interest to ensure\r\n      that\r\n      appropriate measures are in place to protect the confidentiality of all such information. All access to and use of\r\n      PharmaNet and PharmaNet Data is subject to the Act and other applicable laws.\r\n    </p>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      INTERPRETATION\r\n    </p>\r\n    <ol type=\"a\">\r\n      <li>\r\n        <p>\r\n          <strong>Definitions.</strong> Unless otherwise provided, capitalized terms in these Terms of Access will have\r\n          the meanings given below:\r\n        </p>\r\n\r\n        <ul>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Act&quot;</strong> means the <em>Pharmaceutical Services Act</em>.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Approved Site&quot;</strong> means a location within which you provide Health Services and\r\n              which is approved by the Province for PharmaNet access.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Conformance Standards&quot;</strong> means the following documents published by the\r\n              Province, as amended from time to time:\r\n            </p>\r\n            <ol type=\"i\">\r\n              <li>\r\n                <p>\r\n                  PharmaNet Professional and Software Conformance Standards\r\n                </p>\r\n                <a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/software/conformance-standards\">https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/software/conformance-standards</a>:\r\n                and\r\n              </li>\r\n              <li>\r\n                <p>\r\n                  Office of the Chief Information Officer: &quot;Submission for Technical Security Standard and High\r\n                  Level\r\n                  Architecture for Wireless Local Area Network Connectivity&quot;.\r\n                </p>\r\n                <a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/system-access/requirements-for-wireless-access-to-pharmanet \">https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/system-access/requirements-for-wireless-access-to-pharmanet</a>.\r\n              </li>\r\n            </ol>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Health Services&quot;</strong> means &quot;health services&quot; as defined in the\r\n              Information\r\n              Management Regulation.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Information Management Regulation&quot;</strong> means the Information Management\r\n              Regulation,\r\n              B.C. Reg. 328/2021.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Personal Information&quot;</strong> means recorded information about an identifiable\r\n              individual.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;PharmaNet Access Agreement&quot;</strong> means, in respect of an Approved Site, the\r\n              &quot;PharmaNet access agreement&quot; (as defined in the Information Management\r\n              Regulation) between the Province and the owner or operator of the Approved Site.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;PharmaCare Newsletter&quot;</strong>means the PharmaCare newsletter published by the\r\n              Province\r\n              on the following website (or such other website as may be\r\n              specified by the Province from time to time for this purpose):\r\n              <a href=\"www.gov.bc.ca/pharmacarenewsletter\">www.gov.bc.ca/pharmacarenewsletter</a>\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;PharmaNet&quot;</strong> means PharmaNet as continued under section 2 of the Information\r\n              Management Regulation.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;PharmaNet Data&quot;</strong> includes any record or information contained in PharmaNet and\r\n              any record or information obtained from PharmaNet that is\r\n              in your custody, control or possession.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;PRIME&quot;</strong> means the online service provided by the Province that allows users to\r\n              apply for, and manage, their access to PharmaNet,\r\n              and through which users are granted access by the Province.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Province&quot;</strong> means His Majesty the King in Right of British Columbia, as\r\n              represented by the Minister of Health.\r\n            </p>\r\n          </li>\r\n          <li>\r\n            <p>\r\n              <strong>&quot;Professional College&quot;</strong> means the regulatory body governing your provision of\r\n              Health Services.\r\n            </p>\r\n          </li>\r\n        </ul>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Reference to Enactments.</strong> In these Terms of Access, a reference to a statute or regulation by\r\n          name means the statute or regulation of British\r\n          Columbia of that name, as amended or replaced from time to time, and includes any enactment made under the\r\n          authority of that statute or regulation.\r\n        </p>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      ACCESS TERMS\r\n    </p>\r\n    <ol type=\"a\">\r\n      <li>\r\n        <p>\r\n          <strong>Acknowledgement.</strong> You acknowledge that:\r\n        </p>\r\n        <ol type=\"i\">\r\n          <li>\r\n            the Act and the Information Management Regulation govern your PharmaNet access;\r\n          </li>\r\n          <li>\r\n            PharmaNet Data accessed or obtained as a result of such access is disclosed to you by the Province under\r\n            the\r\n            authority of the Act;\r\n          </li>\r\n          <li>\r\n            sections 24 and 25(3) of the Act govern your further use or disclosure of PharmaNet Data; and\r\n          </li>\r\n          <li>\r\n            these terms of access document limits and conditions set, in writing, by the minister, that you are required\r\n            to\r\n            comply with pursuant to section 24(2) of the Act.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Compliance with Applicable Law.</strong> You must comply with the Act, the Information Management\r\n          Regulation and all other laws applicable to PharmaNet access\r\n          and PharmaNet Data.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Written Limits and Conditions of Access.</strong> The following limits and conditions apply to your\r\n          access to PharmaNet:\r\n        </p>\r\n        <ol type=\"i\">\r\n          <li>\r\n            unless (ii) below applies, you must only access PharmaNet when you are physically at an Approved Site, using\r\n            the\r\n            technologies and applications at the Approved Site that are approved by the Province for use for PharmaNet\r\n            access.\r\n          </li>\r\n          <li>\r\n            you may access PharmaNet &quot;remotely&quot; and when you are not physically at the Approved Site only if\r\n            all the following conditions are met:\r\n            <ol type=\"1\">\r\n              <li>\r\n                the owner or operator of the Approved Site has registered you with the Province for remote access\r\n                through the\r\n                Approved Site;\r\n              </li>\r\n              <li>\r\n                you have applied for remote access at the Approved Site using PRIME and are approved by the Province;\r\n              </li>\r\n              <li>\r\n                you are physically located in British Columbia at the time of any such remote access:\r\n              </li>\r\n              <li>\r\n                the remote access technology used at the Approved Site has been specifically approved in writing by the\r\n                Province, and\r\n              </li>\r\n              <li>\r\n                the requirements of the Province’s Policy for Remote Access to PharmaNet\r\n                (https://www2.gov.bc.ca/gov/content/health/practitioner-\r\n                professional-resources/software/conformance-standards) are met.\r\n              </li>\r\n            </ol>\r\n          </li>\r\n          <li>\r\n            you will complete any PharmaNet-related training programs made available to you by the software support\r\n            organization that provides the information technology software and/or services for accessing PharmaNet at\r\n            the Approved\r\n            Site;\r\n          </li>\r\n          <li>\r\n            you acknowledge that the Province may, in writing, communicate additional written limits or conditions\r\n            that apply to\r\n            your access to PharmaNet, in which case you must comply with any such limits or conditions in addition to\r\n            the limits and\r\n            conditions established under these terms of access.\r\n          </li>\r\n          <li>\r\n            you must not permit on-behalf-of users (as defined in the Information Management Regulation) to access\r\n            PharmaNet on\r\n            your behalf.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Privacy and Security Measures.</strong> You will take all reasonable measures to safeguard Personal\r\n          Information, including any Personal Information in the\r\n          PharmaNet Data, while it is in your custody, control or possession. Without limiting the foregoing, you will:\r\n        </p>\r\n        <ol type=\"i\">\r\n          <li>\r\n            secure any workstations used to access PharmaNet and all devices, codes or passwords that enable access to\r\n            PharmaNet;\r\n          </li>\r\n          <li>\r\n            take such other privacy and security measures as the Province may reasonably require from time-to-time.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Conformance Standards.</strong> You will comply with the rules specified in the Conformance Standards\r\n          when accessing and recording information in\r\n          PharmaNet.\r\n        </p>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      DISCLOSURE, STORAGE, AND ACCESS REQUESTS\r\n    </p>\r\n    <ol type=\"a\">\r\n      <li>\r\n        <p><strong>Retention of PharmaNet Data.</strong> You will not store or retain PharmaNet Data in any paper files\r\n          or\r\n          any electronic system, unless such storage or\r\n          retention is required for record keeping in connection with your provisions of Health Services; is in\r\n          accordance\r\n          with\r\n          the requirements of your Professional College; and is otherwise in compliance with the Conformance Standards.\r\n          You will\r\n          not modify any records retained in accordance with this section other than as may be expressly authorized in\r\n          the\r\n          Conformance Standards. For clarity, you may annotate a discrete record provided that the discrete record is\r\n          not\r\n          itself\r\n          modified other than as expressly authorized in the Conformance Standards.</p>\r\n      </li>\r\n      <li>\r\n        <p><strong>Use of Retained Records.</strong> You may use any records retained by you in accordance with section\r\n          4(a) of these Terms of Access for a purpose\r\n          authorized under section 24(1) of the Act.</p>\r\n      </li>\r\n      <li>\r\n        <p><strong>Secondary Use.</strong> Subject to section 4(b) of these Terms of Access, you must not use PharmaNet\r\n          Data for the purpose of quality\r\n          improvement, evaluation, health care planning, surveillance, research, or any purpose other than your\r\n          provision\r\n          of\r\n          Health Services.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p><strong>Disclosure to Third Parties.</strong> You must not disclose PharmaNet Data to any third party unless\r\n          such disclosure is required for your provision of Health\r\n          Services or is otherwise authorized under section 24(1) of the Act.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p><strong>Responding to Patient Access Requests.</strong> Aside from any records retained by you in accordance\r\n          with section 4(a) of these Terms of Access, you will not provide to\r\n          patients any copies of records containing PharmaNet Data or &quot;print outs&quot; produced directly from\r\n          PharmaNet,\r\n          and\r\n          will\r\n          refer any requests for access to such records or &quot;print outs&quot; to the Province.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p><strong>Responding to Requests to Correct a Record contained in PharmaNet.</strong> If you receive a request\r\n          for\r\n          correction of any record or information contained in PharmaNet, you will refer the request\r\n          to the Province.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p><strong>Legal Demands for Records Contained in PharmaNet.</strong> You will immediately notify the Province\r\n          if\r\n          you receive any order, demand or request compelling, or threatening to\r\n          compel, disclosure of records contained in PharmaNet. You will cooperate and consult with the Province in\r\n          responding to\r\n          any such demands. For greater certainty, the foregoing requires that you notify the Province only with respect\r\n          to any\r\n          access requests or demands for records contained in PharmaNet, and not records retained by you in accordance\r\n          with\r\n          section 4(a) of these Terms of Access.\r\n        </p>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      ACCURACY\r\n    </p>\r\n    <p>\r\n      You will make reasonable efforts to ensure that any Personal Information recorded by you in PharmaNet is accurate,\r\n      complete and up to date. In the event that you become aware of a material inaccuracy or error in such information,\r\n      you\r\n      will take reasonable steps to investigate the inaccuracy or error, correct it if necessary, and notify the\r\n      Province of\r\n      the inaccuracy or error and any steps taken.\r\n    </p>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      NOTICE OF NON COMPLIANCE\r\n    </p>\r\n    <ol type=\"a\">\r\n      <li>\r\n        <strong>Non-Compliance.</strong> You will promptly notify the Province, and provide particulars, if:\r\n        <ol type=\"i\">\r\n          <li>\r\n            you do not comply, or you anticipate that you will be unable to comply with these Terms of Access in any\r\n            respect, or\r\n          </li>\r\n          <li>\r\n            you have knowledge of any circumstances, incidents or events which have or may jeopardize the security,\r\n            confidentiality, or integrity of PharmaNet, the provincial drug program, or any government network or\r\n            electronic system\r\n            including any unauthorized attempt, by any person, to access PharmaNet.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Reports to College or Privacy Commissioner.</strong> You acknowledge that the Province may report of\r\n          any material breach of the Act, the Information Management Regulation,\r\n          or these Terms of Access to your Professional College or to the Information and Privacy Commissioner of\r\n          British\r\n          Columbia.\r\n        </p>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      SUSPENSION & TERMINATION OF ACCESS\r\n    </p>\r\n    <ol type=\"a\">\r\n      <li>\r\n        <p><strong>Termination by You for Any Reason.</strong> You may terminate your grant of access to PharmaNet at\r\n          any time on\r\n          written notice to the Province.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Termination of PharmaNet access.</strong> The Province may take administrative action against you in\r\n          relation to\r\n          your grant of access to PharmaNet, including to\r\n          terminate or suspend your access, in accordance with the provisions of the Information Management Regulation.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>Suspension of Account for Inactivity.</strong> As a security precaution, the Province may suspend your\r\n          account\r\n          after a period of inactivity, in accordance with the\r\n          Province’s policies. Please contact the Province immediately if your account has been suspended for inactivity\r\n          but you\r\n          still require access to PharmaNet.\r\n        </p>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      DISCLAIMER OF WARRANTY, LIMITATION OF LIABILITY AND INDEMNITY\r\n    </p>\r\n    <ol type=\"a\">\r\n      <li>\r\n        <p>\r\n          <strong>Information Provided As Is.</strong> You acknowledge and agree that any use of PharmaNet and PharmaNet\r\n          Data is solely at your own risk. All such access and information is provided on an &quot;as is&quot; and\r\n          &quot;as available&quot; basis without warranty or condition of any kind. The Province does not warrant the\r\n          accuracy, completeness or reliability of the PharmaNet Data or the availability of PharmaNet, or that access\r\n          to or the operation of PharmaNet will function without error, failure or interruption.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>You are Responsible.</strong> You are responsible for verifying the accuracy of information disclosed\r\n          to you as a result of your access to PharmaNet or otherwise pursuant to this Agreement before relying or\r\n          acting upon such information. The clinical or other information disclosed to you or an On-Behalf-of User\r\n          pursuant to this Agreement is in no way intended to be a substitute for professional judgment.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>The Province Not Liable for Loss.</strong> No action may be brought by any person against the Province\r\n          for any loss or damage of any kind caused by any reason or purpose related to reliance on PharmaNet or\r\n          PharmaNet Data.\r\n        </p>\r\n      </li>\r\n      <li>\r\n        <p>\r\n          <strong>You Must Indemnify the Province if You Cause a Loss or Claim.</strong> You agree to indemnify and save\r\n          harmless the Province, and the Province’s employees and agents (each an <strong>\"Indemnified Person\"</strong>)\r\n          from any losses, claims, damages, actions, causes of action, costs and expenses that an Indemnified Person may\r\n          sustain, incur, suffer or be put to at any time, either before or after this Agreement ends, which are based\r\n          upon, arise out of or occur directly or indirectly by reason of any act or omission by you, or by any\r\n          On-Behalf-of User, in connection with this Agreement.\r\n        </p>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    <p class=\"bold underline\">\r\n      GENERAL\r\n    </p>\r\n\r\n    <ol type=\"a\">\r\n      <li>\r\n        <p>\r\n          <strong>Notice to Province.</strong> Except where these Terms of Access expressly provides for another method\r\n          of delivery, any notice to be given by you to\r\n          the Province that is contemplated by these Terms of Access must be in writing and emailed or mailed to:\r\n        </p>\r\n\r\n        Director, Information and PharmaNet Development<br/>\r\n        Ministry of Health<br/>\r\n        PO Box 9652, STN PROV GOVT<br/>\r\n        Victoria, BC V8W 9P4<br/>\r\n\r\n        <p>\r\n          Email: <i><a href=\"mailto:PRIMESupport@gov.bc.ca\">PRIMESupport@gov.bc.ca</a></i>\r\n        </p>\r\n\r\n        <p>\r\n          <strong>Notice to You.</strong> Any notice to you to be delivered under these Terms of Access will be in\r\n          writing and delivered by the Province to you\r\n          using any of the contact mechanisms identified by you in PRIME, including by mail to a specified postal\r\n          address, email\r\n          to a specified email address or text message to the specified cell phone number. You may be required to click\r\n          a URL link\r\n          or log into PRIME to receive the content of any such notice.\r\n        </p>\r\n      </li>\r\n      <li>\r\n\r\n        <p>\r\n          <strong>Deemed receipt.</strong>\r\n          Any written communication from a party, if sent electronically, will be deemed to have been received\r\n          immediately, or, if\r\n          sent by mail, will be deemed to have been received three days (excluding Saturdays, Sundays and statutory\r\n          holidays)\r\n          after the date the notice was sent.\r\n        </p>\r\n\r\n      </li>\r\n      <li>\r\n\r\n        <p>\r\n          <strong>Substitute contact information.</strong>\r\n          You may notify the Province of a substitute contact mechanism by updating your contact information in PRIME.\r\n        </p>\r\n\r\n      </li>\r\n      <li>\r\n        <strong>Province may modify Terms of Access.</strong>\r\n        The Province may amend these Terms of Access, including this section, at any time in its sole discretion:\r\n        <ol type=\"i\">\r\n          <li>\r\n            by written notice to you, in which case the amendment will become effective upon the later of (A) the\r\n            date notice of the amendment is first delivered to you, or (B) the effective date of the amendment\r\n            specified by the Province, if any;\r\n            or </li>\r\n          <li>\r\n            by publishing notice of any such amendment in the PharmaCare Newsletter, in which case the notice will\r\n            specify the effective date of the amendment, which date will be at least thirty days after the date\r\n            that the PharmaCare Newsletter containing the notice is first published. </li>\r\n        </ol>\r\n      </li>\r\n    </ol>\r\n\r\n  </li>\r\n</ol>", new DateTimeOffset(new DateTime(2023, 5, 19, 10, 0, 38, 709, DateTimeKind.Unspecified).AddTicks(9033), new TimeSpan(0, -7, 0, 0, 0)), new Guid("00000000-0000-0000-0000-000000000000") });
        }

