            migrationBuilder.InsertData(
                table: "AgreementVersion",
                columns: new[] { "Id", "AgreementType", "CreatedTimeStamp", "CreatedUserId", "EffectiveDate", "Text", "UpdatedTimeStamp", "UpdatedUserId" },
                values: new object[] { 34, 7, new DateTimeOffset(new DateTime(2019, 9, 16, 0, 0, 0, 0, DateTimeKind.Unspecified), new TimeSpan(0, -7, 0, 0, 0)), new Guid("00000000-0000-0000-0000-000000000000"), new DateTimeOffset(new DateTime(2023, 8, 15, 8, 0, 0, 0, DateTimeKind.Unspecified), new TimeSpan(0, 0, 0, 0, 0)), "<h1>ORGANIZATION PHARMANET ACCESS AGREEMENT</h1>\r\n\r\n<p>\r\n  This Organization PharmaNet Access Agreement (the &quot;Agreement&quot;) is executed as of the date first written\r\n  above, by {{organization_name}} (&quot;Organization&quot;) for the benefit of HIS MAJESTY THE KING IN RIGHT OF THE\r\n  PROVINCE OF BRITISH COLUMBIA, as represented by the Minister of\r\n  Health (the &quot;Province&quot;).\r\n</p>\r\n\r\n<p>\r\n  <strong>WHEREAS:</strong>\r\n</p>\r\n\r\n<ol type=\"A\">\r\n  <li>\r\n    The Province owns and is responsible for the operation of PharmaNet, the province-wide network that links health\r\n    care providers to a central data system. Every prescription dispensed in community pharmacies in British Columbia is\r\n    entered into PharmaNet.\r\n  </li>\r\n  <li>\r\n    PharmaNet contains highly sensitive confidential information, including personal information, and it is in the\r\n    public\r\n    interest to ensure that appropriate measures are in place to protect the confidentiality and integrity of such\r\n    information. All access to and use of PharmaNet and PharmaNet Data is subject to the Act and other applicable law.\r\n  </li>\r\n  <li>\r\n    The Province permits authorized users to access PharmaNet to provide health services to the individual whose\r\n    personal information is being accessed.\r\n  </li>\r\n  <li>\r\n    This Agreement sets out the terms by which Organization may permit its Authorized Staff to access PharmaNet in\r\n    connection with the Site(s) operated by Organization.\r\n  </li>\r\n</ol>\r\n\r\n<p>\r\n  <strong>NOW THEREFORE</strong> Organization makes this Agreement knowing that the Province will rely on it in\r\n  permitting access to and use of PharmaNet\r\n  from the Site(s) operated by Organization. Organization conclusively acknowledges that reliance by the Province on\r\n  this Agreement is in every respect justifiable and that it received fair and valuable consideration for this\r\n  Agreement, the receipt and adequacy of which is hereby acknowledged. Organization hereby agrees as follows:\r\n</p>\r\n\r\n<p class=\"text-center\">\r\n  <strong>ARTICLE 1 – INTERPRETATION</strong>\r\n</p>\r\n<ol type=\"1\"\r\n    start=\"1\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          1.1 In this Agreement, terms that are used but not defined will have the same meaning as in the\r\n          <em>Information Management Regulation</em>. The following capitalized terms will have the meaning given below:\r\n        </div>\r\n        <ol type=\"a\">\r\n          <li>\r\n            <strong>&quot;Act&quot;</strong> means the <em>Pharmaceutical Services Act</em>;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Approved&quot;</strong> means approved in writing by an official in the Province’s\r\n            <strong>Pharmaceutical, Laboratory and Blood Services Division</strong>, and\r\n            <strong>&quot;Approval&quot;</strong> will have the same meaning;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Approved SSO&quot;</strong> means, in relation to a Site, the software support organization\r\n            identified\r\n            in the Site Registration as providing Organization with the SSO-Provided Technology used at the Site;\r\n          </li>\r\n\r\n          <li>\r\n            <strong>&quot;Associated Technology&quot;</strong> means, in relation to a Site, the information technology\r\n            hardware,\r\n            software or services used at the Site, other than the SSO-Provided Technology, in connection with PharmaNet\r\n            Access or to store or transmit PharmaNet Data;\r\n          </li>\r\n          <li>\r\n            <P>\r\n              <strong>&quot;Authorized Staff&quot;</strong> means any individual granted access to PharmaNet by the\r\n              Province who:\r\n            </P>\r\n            <ol type=\"i\">\r\n              <li>\r\n                is an employee or independent contractor of Organization,\r\n              </li>\r\n              <li>\r\n                if Organization operates a hospital as defined in section 1 of the <em>Hospital Act</em>, is a member of\r\n                the medical staff of the hospital, or\r\n              </li>\r\n              <li>\r\n                if Organization is an individual, is the Organization;\r\n              </li>\r\n            </ol>\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Conformance Standards&quot;</strong> means the PharmaNet Professional and Software Conformance\r\n            Standards\r\n            (<a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/software/conformance-standards\"\r\n              target=\"_blank\"\r\n              rel=\"noopener noreferrer\">\r\n            https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/software/conformance-standards</a>)\r\n            as published by the Province from time to time;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Control&quot;</strong> means control of greater than fifty percent of the voting rights or\r\n            equity interests in Organization;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Device Provider Staff&quot;</strong> means Authorized Staff that are &quot;device provider\r\n            agents&quot; under the <em>Information Management Regulation</em>;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Information Management Regulation&quot;</strong> means the Information Management Regulation,\r\n            B.C. Reg. 328/2021;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;On-Behalf-Of Staff&quot;</strong> means Authorized Staff that are &quot;on-behalf-of\r\n            users&quot; under the <em>Information Management Regulation</em>;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;PharmaNet Access&quot;</strong> includes Site Access;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;PharmaNet Data&quot;</strong> includes any record or information contained in PharmaNet and\r\n            any record or\r\n            information obtained from PharmaNet that is in the custody, control or possession of Organization or any of\r\n            its employees,\r\n            independent contractors or staff;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;PRIME&quot;</strong> means the online service provided by the Province for the purposes of (i)\r\n            Authorized\r\n            Staff members being granted access to PharmaNet by the Province, and (ii) Organization’s Site Registration.\r\n            Where this\r\n            Agreement refers to submission of information &quot;through PRIME&quot;, that includes the use of any\r\n            paper-based or other alternate\r\n            mechanism provided by the Province for the submission of such information;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Registrant Staff&quot;</strong> means Authorized Staff that are &quot;registrants&quot; under\r\n            the <em>Information Management Regulation</em>;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Signing Authority&quot;</strong> means the individual identified as the &quot;Signing\r\n            Authority&quot; in the Site Registration for a Site;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Site&quot;</strong> means a premises <u>located in British Columbia</u> that is operated by\r\n            Organization and Approved pursuant to section 3.1;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Site Access&quot;</strong> means any access to PharmaNet by an individual when the individual\r\n            is at a Site;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;Site Registration&quot;</strong> means the &quot;site registration&quot; submitted by\r\n            Organization to the Province through PRIME,\r\n            which includes all information required by the Province, all as updated from time to time by Organization in\r\n            accordance with section 5.2 of this Agreement;\r\n          </li>\r\n          <li>\r\n            <strong>&quot;SSO-Provided Technology&quot;</strong> means any information technology hardware, software or\r\n            services provided to Organization by\r\n            an Approved SSO for the purpose of Site Access.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Unless otherwise specified, a reference to a statute or regulation by name means a statute or regulation of\r\n          British Columbia\r\n          of that name, as amended or replaced from time to time, and includes any enactments made under the authority\r\n          of that statute\r\n          or regulation.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          The following is the Schedule attached to and incorporated into this Agreement:\r\n        </div>\r\n        <ul>\r\n          <li>\r\n            Schedule A – Specific Privacy and Security Measures\r\n          </li>\r\n          <li>\r\n            Schedule B – On-Behalf- Staff Site Access Exceptions\r\n          </li>\r\n        </ul>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          The main body of this Agreement, the Schedule, and any documents incorporated by reference into this Agreement\r\n          are to be\r\n          interpreted so that all of the provisions are given as full effect as possible. In the event of a conflict,\r\n          unless\r\n          expressly stated to the contrary the main body of the Agreement will prevail over the Schedule, which will\r\n          prevail over\r\n          any document incorporated by reference.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 2 – REPRESENTATIONS AND WARRANTIES</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"2\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          Organization represents and warrants to the Province, as of the date of this Agreement and throughout its\r\n          term, that:\r\n        </div>\r\n\r\n        <ol type=\"a\">\r\n          <li>\r\n            the information contained in the Site Registration for each Site is true and correct;\r\n          </li>\r\n          <li>\r\n            <p>\r\n              if Organization is not an individual:\r\n            </p>\r\n\r\n            <ol type=\"i\">\r\n              <li>\r\n                Organization has the power and capacity to enter into this Agreement and to comply with its terms;\r\n              </li>\r\n              <li>\r\n                all necessary corporate or other proceedings have been taken to authorize the execution and delivery of\r\n                this Agreement by, or on behalf of, Organization; and\r\n              </li>\r\n              <li>\r\n                this Agreement has been legally and properly executed by, or on behalf of, the Organization and is\r\n                legally binding upon and enforceable against Organization in accordance with its terms.\r\n              </li>\r\n            </ol>\r\n          </li>\r\n        </ol>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 3 – SITE ACCESS REQUIREMENTS</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"3\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          Organization must submit a Site Registration to the Province for each physical location where it intends to\r\n          provide Site Access.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must only provide Site Access if the Site Registration for the Site has been Approved by the\r\n          Province.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must only provide Site Access using SSO-Provided Technology. Such SSO-Provided Technology must\r\n          comply\r\n          with the Conformance Standards and otherwise meet the version and currency requirements of the Province.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Unless otherwise Approved by the Province, Organization must at all times use the secure network or security\r\n          technology\r\n          that the Province certifies or makes available to Organization for the purpose of Site Access. The use of any\r\n          such\r\n          network or technology by Organization may be subject to terms and conditions of use, including acceptable use\r\n          policies,\r\n          established by the Province and communicated to Organization from time to time in writing.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must only allow Site Access by the following users:\r\n        </div>\r\n\r\n        <ol type=\"a\">\r\n          <li>\r\n            Authorized Staff when they are physically located at a Site, and\r\n          </li>\r\n          <li>\r\n            Representatives of an Approved SSO for technical support purposes, in accordance with section 15 of the\r\n            <em>Information Management Regulation</em>.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Except as may be expressly authorized in Schedule B <em>(On-Behalf-Of Staff Site Access Exceptions)</em>,\r\n          Organization must\r\n          ensure that On-Behalf-Of Staff users only access PharmaNet from the Site where the Registrant Staff or Device\r\n          Provider\r\n          Staff user that they are accessing for directly provided health services to the person in respect of whom\r\n          PharmaNet is\r\n          being accessed.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 4</strong>\r\n</p>\r\n<p class=\"text-center\">Article 4 is intentionally left blank</p>\r\n<ol type=\"1\"\r\n    start=\"4\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\"\r\n        class=\" hidden\">\r\n      <li></li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 5 – GENERAL REQUIREMENTS</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"5\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          Organization must comply with the Act and all applicable law.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must immediately notify the Province:\r\n        </div>\r\n        <ol type=\"a\">\r\n          <li>\r\n            if a Site, or any portion thereof, is leased or transferred to another person;\r\n          </li>\r\n          <li>\r\n            if Organization ceases to operate a Site;\r\n          </li>\r\n          <li>\r\n            if Organization acquires a premises that already has PharmaNet access, whether or not Organization intends\r\n            to provide\r\n            Site Access from that location;\r\n          </li>\r\n          <li>\r\n            of any change to the information contained in a Site Registration, including any change to the Site’s\r\n            status, location,\r\n            normal operating hours, Approved SSO, or the name and contact information of the Signing Authority or any of\r\n            the other\r\n            specific roles set out in the Site Registration; and\r\n          </li>\r\n          <li>\r\n            of a change of Control of Organization.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must ensure that Authorized Staff:\r\n        </div>\r\n        <ol type=\"a\">\r\n          <li>\r\n            only access PharmaNet to the extent necessary to provide health services to the individual whose personal\r\n            information is being accessed;\r\n          </li>\r\n          <li>\r\n            only submit claims through PharmaNet if the Site is enrolled as a provider under the Provider Regulation,\r\n            B.C. Reg. 76/2022;\r\n          </li>\r\n          <li>\r\n            first complete any mandatory training program(s) that the Site’s Approved SSO or the Province makes\r\n            available in relation to PharmaNet;\r\n          </li>\r\n          <li>\r\n            access PharmaNet using their own separate login identifications and credentials, and do not share or have\r\n            multiple\r\n            use of any such login identifications and credentials;\r\n          </li>\r\n          <li>\r\n            secure all devices, codes and credentials that enable access to PharmaNet against unauthorized use;\r\n          </li>\r\n          <li>\r\n            are correctly identified as required by the Province on all transactions submitted to PharmaNet, including\r\n            &quot;batch jobs&quot; or similar;\r\n          </li>\r\n          <li>\r\n            only use PharmaNet in accordance with the most current PharmaCare Policy Manual.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          If notified by the Province that an Authorized Staff user’s PharmaNet Access has been suspended or revoked by\r\n          the\r\n          Province, Organization will immediately take any local measures necessary to remove that individual’s\r\n          PharmaNet Access.\r\n          Organization will only restore PharmaNet Access to a previously suspended or revoked Authorized Staff user\r\n          upon the\r\n          Province’s specific written direction.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          The PharmaNet Data disclosed pursuant to this Agreement is disclosed by the Province solely for the Use of the\r\n          Registrant Staff or Device Provider Staff user to whom it is disclosed (or on whose behalf it was obtained by\r\n          an\r\n          On-Behalf-Of Staff user).\r\n        </div>\r\n        <div>\r\n          Organization must not Use any PharmaNet Data, or permit any third party to Use PharmaNet Data, for\r\n          Organization’s (or\r\n          the third party’s) own purposes, except as necessary for the Registrant Staff or Device Provider Staff user’s\r\n          provision\r\n          of health services.\r\n        </div>\r\n        <div>\r\n          For the purposes of this section, &quot;<strong>Use</strong>&quot; includes to collect, access, retain, use,\r\n          process, link, de-identify, modify or\r\n          disclose.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must make reasonable arrangements to protect PharmaNet Data against such risks as unauthorized\r\n          access,\r\n          collection, use, modification, retention, disclosure or disposal, including by:\r\n        </div>\r\n        <ol type=\"a\">\r\n          <li>\r\n            taking all reasonable physical, technical and operational measures necessary at Organization’s Site(s) to\r\n            ensure\r\n            PharmaNet Access operate in accordance with the requirements of Articles 3, 4 and 5 of this Agreement, and\r\n          </li>\r\n          <li>\r\n            complying with the requirements of Schedule A <em>(Specific Privacy and Security Measures)</em>.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 6 – NON-COMPLIANCE AND INVESTIGATIONS</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"6\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          Organization must promptly notify the Province, and provide particulars, if Organization does not comply, or\r\n          anticipates\r\n          that it will be unable to comply, with the terms of this Agreement, or if Organization has knowledge of any\r\n          circumstances, incidents or events which have or may jeopardize the security, confidentiality or integrity of\r\n          PharmaNet,\r\n          including any attempt by any person to gain unauthorized access to PharmaNet or the networks or equipment used\r\n          to\r\n          connect to PharmaNet or convey PharmaNet Data.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must immediately investigate any suspected breaches of this Agreement and take all reasonable\r\n          steps to prevent recurrences of any such breaches.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must cooperate with any audits or investigations conducted by the Province (including any\r\n          independent\r\n          auditor appointed by the Province) regarding compliance with this Agreement, including by providing access\r\n          upon request\r\n          to a Site and any associated facilities, networks, equipment, systems, books, records and personnel for the\r\n          purposes of\r\n          such audit or investigation.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 7 – SITE TERMINATION</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"7\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          Unless otherwise Approved by the Province, Organization must take all measures necessary to prevent further\r\n          PharmaNet\r\n          Access from a Site, or any portion thereof, that is leased or transferred to another person, or otherwise\r\n          ceases to be\r\n          operated by Organization.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          The Province may change the type of PharmaNet Access it provides to a Site at any time and in its sole\r\n          discretion and\r\n          without notice to any person.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          The Province may terminate all PharmaNet Access at a Site immediately, upon notice to the Signing\r\n          Authority for the Site, if:\r\n        </div>\r\n        <ol type=\"a\">\r\n          <li>\r\n            the Approved SSO for the Site is no longer approved by the Province to provide information technology\r\n            hardware, software, or service in connection with PharmaNet, or\r\n          </li>\r\n          <li>\r\n            the Province determines that the SSO-Provided Technology or Associated Technology in use at the Site,\r\n            or any component thereof, is obsolete, unsupported, or otherwise poses an unacceptable security risk,\r\n          </li>\r\n        </ol>\r\n        <div>\r\n          and the Organization is unable or unwilling to remedy the problem within a timeframe acceptable to the\r\n          Province.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          As a security precaution, the Province may suspend PharmaNet Access at a Site after a period of inactivity. If\r\n          PharmaNet\r\n          Access at a Site remains inactive for a period of 90 days or more, the Province may, immediately upon notice\r\n          to the\r\n          Signing Authority for the Site, terminate all further PharmaNet Access at the Site.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must take all measures necessary to prevent further PharmaNet Access at a Site immediately upon\r\n          the Signing\r\n          Authority’s receipt of notice from the Province under sections 7.3 or 7.4.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 8 – TERM AND TERMINATION</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"8\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          The term of this Agreement begins on the date first noted above and continues until it is terminated in\r\n          accordance with this Article 8.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization may terminate this Agreement at any time on written notice to the Province.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          The Province may terminate this Agreement immediately upon notice to Organization if:\r\n        </div>\r\n        <ol type=\"a\">\r\n          <li>\r\n            the Province determines that Organization has failed to comply with any provision of this Agreement;\r\n          </li>\r\n          <li>\r\n            Organization no longer operates any Site from which PharmaNet Access is permitted by the Province; or\r\n          </li>\r\n          <li>\r\n            there is a change of Control of Organization without the Province’s Approval.\r\n          </li>\r\n        </ol>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          In addition to the Province’s right to terminate under section 8.3, the Province may terminate this Agreement\r\n          for any reason upon two (2) months advance notice to Organization.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must prevent any further PharmaNet Access immediately upon termination of this Agreement.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 9 – DISCLAIMER AND INDEMNITY</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"9\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          The PharmaNet Access and PharmaNet Data provided under this Agreement are provided &quot;as is&quot; without\r\n          warranty of\r\n          any kind,\r\n          whether express or implied. All implied warranties, including, without limitation, implied warranties of\r\n          merchantability, fitness for a particular purpose, and non-infringement, are hereby expressly disclaimed. The\r\n          Province\r\n          does not warrant the accuracy, completeness or reliability of the PharmaNet Data or the availability of\r\n          PharmaNet, or\r\n          that access to or the operation of PharmaNet will function without error, failure or interruption.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Under no circumstances will the Province be liable to any person or business entity for any direct, indirect,\r\n          special,\r\n          incidental, consequential, or other damages based on any use of PharmaNet or the PharmaNet Data, including\r\n          without\r\n          limitation any lost profits, business interruption, or loss of programs or information, even if the Province\r\n          has been\r\n          specifically advised of the possibility of such damages.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          Organization must indemnify and save harmless the Province, and the Province’s employees and agents (each\r\n          an &quot;Indemnified Person&quot;) from any losses, claims, damages, actions, causes of action, costs and\r\n          expenses that an Indemnified Person may sustain, incur, suffer or be put to at any time, either before or\r\n          after this Agreement ends, which are based upon, arise out of or occur directly or indirectly by reason of\r\n          any act or omission by Organization, or by any Authorized Staff, in connection with this Agreement.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center \">\r\n  <strong>ARTICLE 10 – GENERAL</strong>\r\n</p>\r\n\r\n<ol type=\"1\"\r\n    start=\"10\"\r\n    class=\"decimal\">\r\n  <li>\r\n    <ol type=\"1\">\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Notice.</strong> Except where this Agreement expressly provides for another method\r\n          of delivery, any notice to be given to the Province must be in writing and mailed or emailed to:\r\n        </div>\r\n\r\n        <address class=\"address\">\r\n          Director, Information and PharmaNet Innovation<br>\r\n          Ministry of Health<br>\r\n          PO Box 9652, STN PROV GOVT<br>\r\n          Victoria, BC V8W 9P4<br>\r\n\r\n          <br>\r\n\r\n          Email: <a href=\"mailto:PRIMESupport@gov.bc.ca\">PRIMESupport@gov.bc.ca</a>\r\n        </address>\r\n        <div>\r\n          Any notice to be given to a Signing Authority or the Organization will be in writing and emailed, mailed,or\r\n          text\r\n          messaged to the Signing Authority (in the case of notice to a Signing Authority) or all Signing Authorities\r\n          (in the case\r\n          of notice to the Organization). A Signing Authority may be required to click a URL link or to log in to PRIME\r\n          to receive\r\n          the content of any such notice.\r\n        </div>\r\n        <div>\r\n          Any written notice from a party, if sent electronically, will be deemed to have been received on the day of\r\n          transmittal\r\n          unless transmitted after the normal business hours of the addressee or on a day that is not a Business Day, in\r\n          which\r\n          case it will be deemed to have been received on the next following Business Day. If sent by mail, the notice\r\n          will be\r\n          deemed to have been received on the third Business Day after its mailing. For the purposes of this section,\r\n          “Business\r\n          Day” means a day, other than a Saturday or Sunday, on which Provincial government offices are open for normal\r\n          business\r\n          in British Columbia.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Assignment.</strong> Organization must not assign any of Organization’s rights or\r\n          obligations under this Agreement without the Province’s Approval.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Waiver.</strong> The failure of the Province at any time to insist on performance of\r\n          any provision of this Agreement by Organization is\r\n          not a waiver of its right subsequently to insist on performance of that or any other provision of this\r\n          Agreement. A\r\n          waiver of any provision or breach of this Agreement is effective only if it is writing and signed by, or on\r\n          behalf of,\r\n          the waiving party.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Modification.</strong> No modification to this Agreement is effective\r\n          unless it is in writing and signed by, or on behalf of, the parties.\r\n        </div>\r\n        <div>\r\n          Notwithstanding the foregoing, the Province may unilaterally amend this Agreement, including the Schedule and\r\n          this\r\n          section, at any time in its sole discretion, by written notice to Organization, in which case the amendment\r\n          will become\r\n          effective upon the later of: (i) the date notice of the amendment is delivered to Organization; and (ii) the\r\n          effective\r\n          date of the amendment specified by the Province. The Province will make reasonable efforts to provide at least\r\n          thirty\r\n          (30) days advance notice of any such amendment, subject to any determination by the Province that a shorter\r\n          notice\r\n          period is necessary due to changes in the Act, applicable law or applicable policies of the Province, or is\r\n          necessary to\r\n          maintain privacy and security in relation to PharmaNet or PharmaNet Data.\r\n        </div>\r\n        <div>\r\n          If Organization does not agree with any amendment for which notice has been provided by the Province in\r\n          accordance with\r\n          this section, Organization must promptly (and in any event prior to the effective date) cease PharmaNet Access\r\n          at all\r\n          Sites and take the steps necessary to terminate this Agreement in accordance with Article 8.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Time is of the Essence.</strong> Time is of the essence in this Agreement.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Relationship of the Parties.</strong> This Agreement does not create any agency,\r\n          partnership or joint venture between the Parties.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Entire Agreement.</strong> This Agreement constitutes the entire agreement\r\n          between the Parties and supersedes all other agreements between the Parties relating to its subject matter.\r\n        </div>\r\n      </li>\r\n      <li>\r\n        <div>\r\n          <strong class=\"underline\">Governing Law.</strong> This Agreement will be governed by and will be\r\n          construed and interpreted in accordance with the laws of British Columbia and the laws of Canada applicable\r\n          therein.\r\n        </div>\r\n      </li>\r\n    </ol>\r\n  </li>\r\n</ol>\r\n\r\n{{signature_block}}\r\n\r\n<p class=\"text-center break-before\">\r\n  <strong>SCHEDULE A – SPECIFIC PRIVACY AND SECURITY MEASURES</strong>\r\n</p>\r\n\r\n<p>\r\n  Organization must, in relation to each Site and in relation to PharmaNet Access:\r\n</p>\r\n\r\n<ol type=\"a\">\r\n  <li>\r\n    secure all workstations and printers at the Site to prevent any viewing of PharmaNet Data by persons other than\r\n    Authorized Staff;\r\n  </li>\r\n  <li>\r\n    <p>\r\n      implement all privacy and security measures specified in the following documents published by the Province, as\r\n      amended from time to time:\r\n    </p>\r\n\r\n    <ol type=\"i\">\r\n      <li>\r\n        <p>\r\n          the Conformance Standards\r\n        </p>\r\n\r\n        (<a href=\"https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/software/conformance-standards\"\r\n           target=\"_blank\"\r\n           rel=\"noopener noreferrer\">\r\n          https://www2.gov.bc.ca/gov/content/health/practitioner-professional-resources/software/conformance-standards\r\n        </a>)\r\n      </li>\r\n\r\n    </ol>\r\n  </li>\r\n  <li>\r\n    ensure that a qualified technical support person is engaged to provide security support for the Site. This person\r\n    should be familiar with the Site’s network configurations, hardware and software, including all SSO-Provided\r\n    Technology\r\n    and Associated Technology, and should be capable of understanding and adhering to the standards set forth in this\r\n    Agreement and Schedule. Note that any such qualified technical support person must not be permitted by Organization\r\n    to access or use PharmaNet in any manner, except as expressly permitted under this Agreement;\r\n  </li>\r\n  <li>\r\n    establish and maintain documented privacy policies that detail how Organization will meet its privacy obligations in\r\n    relation to the Site;\r\n  </li>\r\n  <li>\r\n    establish breach reporting and response processes in relation to the Site;\r\n  </li>\r\n  <li>\r\n    detail expectations for privacy protection in contracts and service agreements as applicable at the Site;\r\n  </li>\r\n  <li>\r\n    regularly review the administrative, physical and technological safeguards at the Site;\r\n  </li>\r\n  <li>\r\n    establish and maintain a program for monitoring PharmaNet Access, including by making appropriate monitoring and\r\n    reporting mechanisms available to Authorized Staff for this purpose.\r\n  </li>\r\n</ol>\r\n\r\n<p class=\"text-center break-before\">\r\n  <strong>SCHEDULE B – ON-BEHALF- STAFF SITE ACCESS EXCEPTIONS</strong>\r\n</p>\r\n<p>\r\n  On-Behalf-Of Staff acting on behalf of a Device Provider Staff user may access PharmaNet at a Site that is different\r\n  from the one where the Device Provider Staff user provided health services to the person in respect of whom PharmaNet\r\n  is\r\n  being accessed only if that different Site is registered by the Device Provider Staff user for the purpose of their\r\n  submission of claims as permitted under the Provider Regulation.\r\n</p>", new DateTimeOffset(new DateTime(2019, 9, 16, 0, 0, 0, 0, DateTimeKind.Unspecified), new TimeSpan(0, -7, 0, 0, 0)), new Guid("00000000-0000-0000-0000-000000000000") });
        }

