    VALUES (4, TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '00000000-0000-0000-0000-000000000000', 4, TIMESTAMPTZ '2019-09-16 00:00:00-07:00', 'To: PharmNet Access administrator <br> <br> @Model.EnrolleeFullName has been approved for <strong>Health Authority Access to PharmaNet.</strong> <br> They can now be set up with their PharmaNet Access account in your local software. You must include their <strong>Global PharmaNet ID (GPID)</strong> on their account profile. <br> You can access their GPID via this link here. <br> <a href="@Model.TokenUrl">@Model.TokenUrl</a> <br> <strong>This link will expire after @Model.ExpiresInDays days</strong>.', TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '00000000-0000-0000-0000-000000000000');
    INSERT INTO "EmailTemplate" ("Id", "CreatedTimeStamp", "CreatedUserId", "EmailType", "ModifiedDate", "Template", "UpdatedTimeStamp", "UpdatedUserId")
    VALUES (3, TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '00000000-0000-0000-0000-000000000000', 3, TIMESTAMPTZ '2019-09-16 00:00:00-07:00', 'To: Whom it may concern, <br> <br> @Model.EnrolleeFullName has been approved for <strong>Community Pharmacy Access to PharmaNet.</strong> They can now be set up with their PharmaNet Access account in your local software. You must include their <strong>Global PharmaNet ID (GPID)</strong> on their account profile. You can access their GPID via this link below. <br> <br> <a href="@Model.TokenUrl">@Model.TokenUrl</a> <br> <strong>This link will expire after @Model.ExpiresInDays days</strong>. <br> <br> Thank you.', TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '00000000-0000-0000-0000-000000000000');
    INSERT INTO "EmailTemplate" ("Id", "CreatedTimeStamp", "CreatedUserId", "EmailType", "ModifiedDate", "Template", "UpdatedTimeStamp", "UpdatedUserId")
    VALUES (2, TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '00000000-0000-0000-0000-000000000000', 2, TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '<style> .underline { text-decoration: underline; } .list-item-mb { margin-bottom: 0.75rem; } </style>To: Clinic Manager (person responsible for coordinating PharmaNet access):<br><br>@Model.EnrolleeFullName has been approved for Private Community Health Practice Access to PharmaNet.<br><br> <strong> To set up their access, you must forward this PRIME Enrolment Confirmation and the information specified below to your <span class="underline">PharmaNet Software Vendor</span>. </strong> <br><br> <ol> <li class="list-item-mb"> Name of medical clinic: </li> <li class="list-item-mb"> Clinic address: </li> <li class="list-item-mb"> Pharmacy equivalency code (PEC): <i>This is your PharmaNet site ID; ask your vendor if you don’t know it:</i> </li> <li class="list-item-mb"> For <strong> physicians, pharmacists, and nurse practitioners:</strong> <br><br> College name and College ID of this user <em>(leave blank if this user is not a physician, pharmacist or nurse practitioner)</em> </li> <li class="list-item-mb"> For users who work <strong>On Behalf Of</strong> a physician, pharmacist, or nurse practitioner: <br><br> College name(s) and College ID(s) of the physicians, pharmacists or nurse practitioners who this user will access PharmaNet on behalf of: <em>(leave this blank if this user is a pharmacist, nurse practitioner or physician)</em> </li> </ol> <a href="@Model.TokenUrl">@Model.TokenUrl</a> <br> <strong>This link will expire after @Model.ExpiresInDays days.</strong> <br><br> Thank you,<br><br> PRIME<br><br> 1-844-397-7463<br><br> <a href=''mailto:PRIMEsupport@gov.bc.ca'' target=''_top''>PRIMEsupport@gov.bc.ca</a>', TIMESTAMPTZ '2019-09-16 00:00:00-07:00', '00000000-0000-0000-0000-000000000000');
